function addItem(){var n=$("#add-item-title").val(),t=$("#add-item-dueDate").val();$.post("/Todo/AddItem",{title:n,dueDate:t},function(){window.location="/Todo"}).fail(function(n){if(n&&n.responseJSON){var t=n.responseJSON[Object.keys(n.responseJSON)[0]];$("#add-item-error").text(t);$("#add-item-error").show()}})}$(document).ready(function(){$("#add-item-error").hide();$("#add-item-button").on("click",addItem)});